import { Link, useRouteLoaderData, useSubmit, useLoaderData, json, defer } from 'react-router-dom';

import classes from './OfferItem.module.css';
import ProductsTable from './ProductsTable';

function OfferItem({ offer }) {
  const token = useRouteLoaderData('root');
  const submit = useSubmit();

  function startDeleteHandler() {
    const proceed = window.confirm('Are you sure?');

    if (proceed) {
      submit(null, { method: 'delete' });
    }
  }

  let enrichedDetailProducts = useLoaderData(offer.detailProducts);




  return (
    <article className={classes.offer}>
      <img src={offer.bigPhotoURL} alt={offer.shortName} />
      <h1>Short Name: {offer.shortName}</h1>
      <h1>Large Name: {offer.largeName}</h1>
      <h1>List Price: {offer.listPrice}</h1>
      <h1>Offer Price: {offer.offerPrice}</h1>
      <h1>Discount: {offer.discount}</h1>
      <h1>Offer Type: {offer.offerType}</h1>
      <h1>Status: {offer.status}</h1>
      <div>
        <ProductsTable products={enrichedDetailProducts} />;
      </div>
      {token && (
        <menu className={classes.actions}>
          <Link to="edit">Edit</Link>
          <button onClick={startDeleteHandler}>Delete</button>
        </menu>
      )}
    </article>
  );
}

export default OfferItem;


async function getProduct(id){

  const response = await fetch('http://localhost:8095/product/'+ id);
    console.log(response);
    if (!response.ok) {
      // return { isError: true, message: 'Could not fetch events.' };
      // throw new Response(JSON.stringify({ message: 'Could not fetch events.' }), {
      //   status: 500,
      // });
      throw json(
        { message: 'Could not fetch offers.' },
        {
          status: 500,
        }
      );
    } else {
      const resData = await response.json();
      console.log("from get product");
      console.log(resData);
      return resData.listing[0];
    }
}

function loadProduct(detailProducts) {

  let enrichedDetailProducts = [];
  let product = {};

  detailProducts.forEach((item) => {

    product = getProduct(item.productId)
    item.shortName = product.shortName;
    item.largeName = product.largeName;
    item.description = product.description;
    item.detailedDescription = product.detailedDescription;
    item.smallPhotoURL = product.smallPhotoURL;
    item.mediumPhotoURL = product.mediumPhotoURL;
    item.bigPhotoURL = product.bigPhotoURL;
    item.status =  product.status;
    enrichedDetailProducts.push(item);


  });
  return enrichedDetailProducts;

}

export function loader(productId) {
  return defer({
    offers: loadProduct(productId),
  });
}
