import { Suspense } from 'react';
import {
  useRouteLoaderData,
  json,
  redirect,
  defer,
  Await,
} from 'react-router-dom';

import OfferItem from '../components/OfferItem';
import OfferList from '../components/OfferList';
import { getAuthToken } from '../util/auth';

function OfferDetailPage() {
  const { offer, offers } = useRouteLoaderData('offer-detail');

  return (
    <>
      <Suspense fallback={<p style={{ textAlign: 'center' }}>Loading...</p>}>
        <Await resolve={offer}>
          {(loadedOffer) => <OfferItem offer={loadedOffer} />}
        </Await>
      </Suspense>
    </>
  );
}

export default OfferDetailPage;

async function loadOffer(id) {
  const response = await fetch('http://localhost:8095/offer/offer?id=' + id);

  if (!response.ok) {
    throw json(
      { message: 'Could not fetch details for selected offer.' },
      {
        status: 500,
      }
    );
  } else {
    const resData = await response.json();
    console.log(resData)
    return resData.offer;
  }
}

async function loadOffers() {
  const response = await fetch('http://localhost:8095/offer/offers');

  if (!response.ok) {
    // return { isError: true, message: 'Could not fetch events.' };
    // throw new Response(JSON.stringify({ message: 'Could not fetch events.' }), {
    //   status: 500,
    // });
    throw json(
      { message: 'Could not fetch offers.' },
      {
        status: 500,
      }
    );
  } else {
    const resData = await response.json();
    return resData.offers;
  }
}

export async function loader({ request, params }) {
  const id = params.offerId;

  return defer({
    offer: await loadOffer(id),
    offers: loadOffers(),
  });
}

export async function action({ params, request }) {
  const offerId = params.offerId;

  const token = getAuthToken();
  const response = await fetch('http://localhost:8090/offers/' + offerId, {
    method: request.method,
    headers: {
      'Authorization': 'Bearer ' + token
    }
  });

  if (!response.ok) {
    throw json(
      { message: 'Could not delete offer.' },
      {
        status: 500,
      }
    );
  }
  return redirect('/offer/offers');
}
